mapper.select:
select c.id, c.article_id, a.title, a.short_title, c.column_id, col.column_name as `column`, col.cover as column_cover, c.`section` as sort, c.create_time from column_article as c left join article as a on c.article_id = a.id left join column_info as col on c.column_id = col.id where a.deleted = ${@com.github.paicoding.forum.api.model.enums.YesOrNoEnum@NO.code}
and c.column_id = #{columnId}
and a.short_title like concat('%', #{articleTitle}, '%')
order by c.section asc

select count(1) from column_article as c left join article as a on c.article_id = a.id left join column_info as col on c.column_id = col.id where a.deleted = ${@com.github.paicoding.forum.api.model.enums.YesOrNoEnum@NO.code}
and c.column_id = #{columnId}
and a.short_title like concat('%', #{articleTitle}, '%')
